import React from "react";
import QandA from "./QandA";

class Challenge extends React.Component {
  constructor(props) {
    super(props);
  }

  render() {
    const { questionPackage, step, onNextStep } = this.props;
    // this is how youdestructure in class component - get rid of this.props
    return (
      <div className="ui cards">
        <div className="card">
          <div className="content"></div>
          <h1>step={step}</h1>
          {questionPackages.map(questionPackage => (
            <QandA questionPackage={questionPackage} onNextStep={onNextStep} />
          ))}
        </div>
      </div>
    );
  }
}

export default Challenge;
