import React from "react";

const AnswerButs = props => {
  const success = butId => {
    butId == 123 ? "well done!" : "bad luck!";
  };

  return (
    <>
      <div className="ui cards">
        <div className="card">
          <div className="content">
            <div className="meta">
              <div className="ui four column grid">
                <div className="row">
                  <div className="column">
                    <button
                      className="ui button"
                      value="0"
                      onClick={success(0)}
                    >
                      {props.mixedAnswers[0]}
                    </button>
                  </div>
                  <div className="column">
                    <button className="ui button" value="1">
                      {props.mixedAnswers[1]}
                    </button>
                  </div>
                </div>
                <div className="column">
                  <button className="ui button" value="2">
                    {props.mixedAnswers[2]}
                  </button>
                </div>
                <div className="column">
                  <button className="ui button" value="3">
                    {props.mixedAnswers[3]}
                  </button>
                </div>
              </div>
            </div>
            <div className="description" />
            {props.QuestionFromAPI}
          </div>
        </div>
      </div>
    </>
  );
};

export default AnswerButs;
