import React from "react";

const AnswerButs = ({ mixedAnswers }) => {
  const success = butId => {
    if (butId == 122) {
      alert("well done!");
    } else {
      alert("bad luck!");
    }
  };

  return (
    <>
      <div className="ui cards">
        <div className="card">
          <div className="content">
            <div className="meta">
              <div className="ui four column grid">
                <div className="row">
                  <div className="column">
                    <button onClick={success} className="ui button">
                      {mixedAnswers[0]}
                    </button>
                  </div>
                  <div className="column">
                    <button className="ui button">{mixedAnswers[1]}</button>
                  </div>
                </div>
                <div className="column">
                  <button className="ui button">{mixedAnswers[2]}</button>
                </div>
                <div className="column">
                  <button className="ui button">{mixedAnswers[3]}</button>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default AnswerButs;
